class QuickReact(commands.Cog):
    def __init__(self, bot):
        self.bot = bot
        self.emoji = None
        self.message = None
        self.quick_react.start()
        self.remaining_winnings = []
        self.lock = asyncio.Lock()
        self.already_won = []

    def cog_unload(self):
        self.quick_react.cancel()

    @tasks.loop(seconds=10)
    async def quick_react(self):
        await self.bot.wait_until_ready()
        self.quick_react.change_interval(minutes=random.randint(
            self.bot.config["min_minutes"], self.bot.config["max_minutes"]))
        if not self.emoji:
            self.emoji = random.choice(emojis)
            return
        await self.do_quick_react(self.bot.get_channel(self.bot.config["channel"]))

    async def do_quick_react(self, channel):
        async with self.lock:
            emoji = random.choice(emojis)
            self.emoji = emoji
            embed = discord.Embed(
                title="Quick React!",
                description=f"React with {emoji} to win Points!"
            )
            embed.set_footer(
                text=f"1st place gets {self.bot.config['winnings'][0]} Points!")
            self.message = await channel.send(embed=embed)
            self.remaining_winnings = self.bot.config["winnings"][::]
            self.already_won = []

    @commands.command()
    @commands.has_guild_permissions(administrator=True)
    async def quickreact(self, ctx):
        """
        Starts a quick react in the current channel
        """
        await self.do_quick_react(ctx.channel)

    @commands.Cog.listener()
    async def on_raw_reaction_add(self, payload):
        if str(payload.emoji) == self.emoji and payload.message_id == self.message.id and payload.user_id not in self.already_won:
            async with self.lock:
                if self.remaining_winnings != []:
                    win = self.remaining_winnings.pop(0)
                    user = await self.bot.fetch_user(payload.user_id)
                    try:
                        await user.send(f"**Congrats! You won {win} points!**")
                    except:
                        pass

                    r = await self.bot.db.points.find_one({"user": user.id})
                    if r:
                        await self.bot.db.points.find_one_and_update({"user": user.id},  {"$inc": {"points":  win}})
                    else:
                        await self.bot.db.points.insert_one({"user": user.id, "points": win})
                    self.already_won.append(payload.user_id)


def setup(bot):
    bot.add_cog(QuickReact(bot))
